import React from 'react';
import jsPDF from 'jspdf';

const PdfGenerator = ({ dataset }) => {
  const generatePDF = () => {
    const pdf = new jsPDF();
    pdf.text('Relatório de Dados', 10, 10);

    // Loop através do dataset para adicionar dados ao PDF
    dataset.forEach(data => {
      pdf.text(`Ano: ${data.year}, Ganho: ${data.userGain}, Perda: ${data.userLost}`, 10, pdf.previousAutoTable.finalY + 10);
    });

    // Salvar ou exibir o PDF
    pdf.save('relatorio.pdf');
  };

  return (
    <div>
      <button onClick={generatePDF}>Gerar PDF</button>
    </div>
  );
};

export default PdfGenerator;
